function GuidedSession({ cat, onClose }) {
  const [step, setStep] = useState(0);
  return (
    <div style={{ position:"fixed",inset:0,background:"rgba(28,43,74,0.75)",display:"flex",alignItems:"center",justifyContent:"center",zIndex:200,backdropFilter:"blur(4px)" }}>
      <div style={{ background:C.white,borderRadius:24,padding:40,maxWidth:500,width:"90%",boxShadow:"0 24px 60px rgba(28,43,74,0.25)" }}>
        <div style={{ fontSize:12,color:C.textGray,letterSpacing:2,textTransform:"uppercase",marginBottom:4 }}>Guided Session</div>
        <div style={{ fontSize:22,fontWeight:700,color:C.navy,fontFamily:"Georgia,serif",marginBottom:20 }}>{cat.label}</div>
        <div style={{ height:4,background:C.lightGray,borderRadius:4,marginBottom:20 }}>
          <div style={{ height:4,borderRadius:4,background:`linear-gradient(90deg,${cat.color},${C.mint})`,width:`${((step+1)/cat.questions.length)*100}%`,transition:"width 0.4s" }} />
        </div>
        <div style={{ fontSize:13,color:C.textGray,marginBottom:10 }}>Question {step+1} of {cat.questions.length}</div>
        <div style={{ background:cat.bg,borderRadius:16,padding:24,marginBottom:28,fontSize:18,color:C.navy,lineHeight:1.6,fontFamily:"Georgia,serif",minHeight:80,display:"flex",alignItems:"center" }}>
          "{cat.questions[step]}"
        </div>
        <div style={{ display:"flex",gap:10,justifyContent:"space-between" }}>
          <button onClick={onClose} style={{ padding:"10px 18px",borderRadius:12,border:`1px solid ${C.midGray}`,background:"transparent",color:C.textGray,cursor:"pointer",fontSize:14,fontFamily:"inherit" }}>End Session</button>
          <div style={{ display:"flex",gap:8 }}>
            {step > 0 && <button onClick={()=>setStep(step-1)} style={{ padding:"10px 18px",borderRadius:12,border:`1px solid ${C.midGray}`,background:"transparent",color:C.navy,cursor:"pointer",fontSize:14,fontFamily:"inherit" }}>Back</button>}
            {step < cat.questions.length-1
              ? <button onClick={()=>setStep(step+1)} style={{ padding:"10px 22px",borderRadius:12,border:"none",background:`linear-gradient(135deg,${cat.color},${C.mint})`,color:C.white,cursor:"pointer",fontSize:14,fontWeight:600,fontFamily:"inherit" }}>Next</button>
              : <button onClick={onClose} style={{ padding:"10px 22px",borderRadius:12,border:"none",background:C.navy,color:C.white,cursor:"pointer",fontSize:14,fontWeight:600,fontFamily:"inherit" }}>Finish</button>
            }
          </div>
        </div>
      </div>
    </div>
  );
}

